import React, { useState, useEffect } from "react";
import Card from "./components/Card";
import CardPlaceholder from "./components/CardPlaceHolder";

import "./styles/styles.css";

export default function App() {
  const [theme, setTheme] = useState("light");
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // simulating a server call that would take some time
    setTimeout(
      () =>
        setUsers([
          {
            name: "John",
            age: 17,
            company: "Google",
            phone: "566498498498",
            email: "john@gmail.com",
          },
          {
            name: "Doe",
            age: 23,
            company: "Facebook",
            phone: "3456789",
            email: "doe@gmail.com",
          },
        ]),
      5000
    );
  }, []);

  return (
    <div className="App">
      {users.length ? (
        users.map((user, index) => (
          <Card
            name={user.name}
            age={user.age}
            company={user.company}
            phone={user.phone}
            email={user.email}
            theme={theme}
          />
        ))
      ) : (
        <div>
          <CardPlaceholder />
          <CardPlaceholder />
          <CardPlaceholder />
        </div>
      )}

      <button
        onClick={() => {
          if (theme === "light") setTheme("dark");
          else setTheme("light");
        }}
      >
        Toggle Theme
      </button>
    </div>
  );
}
